function Carousel() {
  const [currentSlide, setCurrentSlide] = useState(0);

  const totalSlides = Math.ceil(cities.length / 4);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentSlide((currentSlide + 1) % totalSlides);
    }, 5000);
    return () => clearInterval(interval);
  }, [currentSlide, totalSlides]);

  const nextSlide = () => {
    setCurrentSlide((currentSlide + 1) % totalSlides);
  };

  const prevSlide = () => {
    setCurrentSlide((currentSlide - 1 + totalSlides) % totalSlides);
  };

  const getCurrentCities = () => {
    const startIndex = currentSlide * 4;
    return cities.slice(startIndex, startIndex + 4);
  };

  return (
    <div className="my-32 shadow-2xl w-[80vw] h-auto flex flex-col justify-center items-center">
      <h2 className="text-5xl font-bold text-center">Popular Mytineraries</h2>
      <div className="relative m-5">
        <button
          onClick={prevSlide}
          className="absolute left-0 top-1/2 transform -translate-y-1/2 bg-gray-800 text-white p-2"
        >
          &#10094;
        </button>
        <div className="flex justify-center space-x-4">
          {getCurrentCities().map((city, index) => (
            <div key={index} className="text-center">
              <img
                src={city.image}
                alt={city.name}
                className="w-48 h-32 object-cover"
              />
              <p className="mt-2">{city.name}</p>
            </div>
          ))}
        </div>
        <button
          onClick={nextSlide}
          className="absolute right-0 top-1/2 transform -translate-y-1/2 bg-gray-800 text-white p-2"
        >
          &#10095;
        </button>
      </div>
      <div className="w-[60vw] h-auto">
        <CallToAction />
      </div>
    </div>
  );
}
function CallToAction() {
  return (
    <div className="">
      <h2 className="text-lg font-bold text-center">My Tinerary</h2>
      <p>
        "Explore and create your next unforgettable journey! Discover unique
        itineraries and customize them to your liking. Click on Get Inspired Now
        and start planning the adventure of your dreams."
      </p>
      <div className="flex justify-end my-3">
        <Link
          to="/cities"
          className="bg-blue-500 text-white px-3 py-3 rounded-full text-xs hover:bg-blue-600"
        >
          Explore Cities
        </Link>
      </div>
    </div>
  );
}

export default Carousel;
